<template>
  <div class="home container">
    <!-- If not authenticated show log in / sign up-->
    <div v-if="!$auth.isAuthenticated">
      <div id="welcome">
        <div class="row">
          <div class="col">
            <h1>{{ $t("welcome.welcomeMessage") }}</h1>
          </div>
        </div>
        <div class="row">
          <div class="col">
            <img
              src="data:image/png;base64,"
            />
          </div>
        </div>
        <div class="row">
          <div class="col">
            <div v-if="!$auth.loading">
              <CommuteStatusButton
                :on-click="loginButtonAction"
                :button-text="$t('welcome.loginButton')"
                :button-size="'lg'"
              />
            </div>
          </div>
        </div>
        <div class="row">
          <div class="col">
            <CommuteStatusButton
              :on-click="signupButtonAction"
              :button-text="$t('welcome.signupButton')"
              :button-style="'primary'"
              :button-size="'lg'"
            />
          </div>
        </div>
      </div>
    </div>
  </div>
</template>

<script>
import CommuteStatusButton from "@/components/CommuteStatusButton.vue";

export default {
  name: "Home",
  mounted() {
    this.redirectIfLoggedIn();
  },
  components: {
    CommuteStatusButton
  },
  methods: {
    redirectIfLoggedIn() {
      if (!this.$auth.loading && this.$auth.isAuthenticated) {
        this.$router.push("commutestatus");
      }
    },
    loginButtonAction() {
      this.$auth.loginWithRedirect();
    },
    signupButtonAction() {
      alert("I need to start this js");
    }
  }
};
</script>
<style scoped lang="scss">
#welcome {
  padding: 30px;
}</style
>e
